import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import NoSsr from '@material-ui/core/NoSsr';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { Document, Page } from 'react-pdf'

function TabContainer(props) {
  return (
    <Typography component="div" style={{ padding: 8 * 3 }}>
      {props.children}
    </Typography>
  );
}

// TabContainer.propTypes = {
//   children: PropTypes.node.isRequired,
// };

function LinkTab(props) {
  return <Tab component="a" onClick={event => event.preventDefault()} {...props} />;
}

const styles = theme => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
  tabsRoot: {
    borderBottom: '1px solid #e8e8e8',
    backgroundColor: theme.palette.background.paper,
  },
  tabsIndicator: {
    backgroundColor: '#1890ff',
  },
  tabRoot: {
    color: '#1890ff',
    textTransform: 'initial',
    minWidth: 72,
    fontWeight: theme.typography.fontWeightRegular,
    marginRight: theme.spacing.unit * 4,
    fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ].join(','),
    '&:hover': {
      color: '#40a9ff',
      opacity: 1,
    },
    '&$tabSelected': {
      color: '#1890ff',
      fontWeight: theme.typography.fontWeightMedium,
    },
    '&:focus': {
      color: '#40a9ff',
    },
  },
  tabSelected: {},
  typography: {
    padding: theme.spacing.unit * 3,
  },
  card: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

class App extends Component {

  state = {
    value: 0,
  };

  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {

    const { classes } = this.props;
    const { value } = this.state;

    return (
      <NoSsr>
        <div className={classes.root}>
          <AppBar position="static">
            <Tabs fullWidth value={value} onChange={this.handleChange} classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}>
              <LinkTab classes={{ root: classes.tabRoot, selected: classes.tabSelected }} label="Home" href="home" />
              <LinkTab classes={{ root: classes.tabRoot, selected: classes.tabSelected }} label="Projects" href="projects" />
              <LinkTab classes={{ root: classes.tabRoot, selected: classes.tabSelected }} label="Resume" href="resume" />
            </Tabs>
          </AppBar>

          {value === 0 && <TabContainer>
            Profile
            <p id="bio">
              I&apos;m a 19-year-old Computer Science student at UC Berkeley. As a full-stack
              developer, I&apos;ve created multiple cool projects and worked for companies like
              SpaceX and BMW.
              <br/><br/>
              On campus, I&apos;m heavily involved with <a href="https://blockchain.berkeley.edu/" target="_blank">
              Blockchain at Berkeley</a> and its consulting arm, and right now I&apos;m working on a project
              for Ford. I&apos;m absolutely fascinated by cybersecurity, distributed networks, deep learning,
              and computer vision.
              <br/><br/>
              Outside of the classroom, you can usually find me pitching to investors or working
              on <a href="https://bidsevents.com" target="_blank">my company</a>. I&apos;ve created
              tons of educational content, ghostwritten technical whitepapers for various companies,
              and delivered presentations to executives three times my age. Companies I&apos;ve started in
              the past have gone through YCombinator&apos;s Startup School and served thousands of users.
              Unfortunately, a lot of the work I&apos;ve done is proprietary information, but feel free to
              check out some of the projects for which I can disclose details in the
              <a onclick="document.getElementById(&apos;projectsTab&apos;).click()">
              Projects</a> tab.
              <br/><br/>
              I&apos;m looking for an internship for the summer of 2019, so make sure to <a href="https://rjotwani.youcanbook.me/" target="_blank">
              book me</a> while you still can!
            </p>
            <Card className={classes.card}>
              <CardActionArea>
                <CardMedia
                  className={classes.media}
                  image=".images/ravi.jpg"
                  title="Contemplative Reptile"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="h2">
                    Hi, I&apos;m Ravi.
                  </Typography>
                  <Typography component="p">
                    I&apos;m a 19-year-old Computer Science student at UC Berkeley. As a full-stack
                    developer, I&apos;ve created multiple cool projects and worked for companies like
                    SpaceX and BMW.
                    <br/><br/>
                    On campus, I&apos;m heavily involved with <a href="https://blockchain.berkeley.edu/" target="_blank">
                    Blockchain at Berkeley</a> and its consulting arm, and right now I&apos;m working on a project
                    for Ford. I&apos;m absolutely fascinated by cybersecurity, distributed networks, deep learning,
                    and computer vision.
                    <br/><br/>
                    Outside of the classroom, you can usually find me pitching to investors or working
                    on <a href="https://bidsevents.com" target="_blank">my company</a>. I&apos;ve created
                    tons of educational content, ghostwritten technical whitepapers for various companies,
                    and delivered presentations to executives three times my age. Companies I&apos;ve started in
                    the past have gone through YCombinator&apos;s Startup School and served thousands of users.
                    Unfortunately, a lot of the work I&apos;ve done is proprietary information, but feel free to
                    check out some of the projects for which I can disclose details in the
                    <a onclick="document.getElementById(&apos;projectsTab&apos;).click()">
                    Projects</a> tab.
                    <br/><br/>
                    I&apos;m looking for an internship for the summer of 2019, so make sure to <a href="https://rjotwani.youcanbook.me/" target="_blank">
                    book me</a> while you still can!
                  </Typography>
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  Share
                </Button>
                <Button size="small" color="primary">
                  Learn More
                </Button>
              </CardActions>
            </Card>
          </TabContainer>}

          {value === 1 && <TabContainer>
            Projects
          </TabContainer>}

          {value === 2 && <TabContainer>
            <Document file="./resume.pdf">
              <Page pageNumber="1" />
            </Document>
          </TabContainer>}

        </div>
      </NoSsr>
    );
  }
}

export default withStyles(styles)(App);
